import React, { useState } from 'react';
import './App.css';
import RandomCocktail from './RandomCocktail';
import CocktailSearch from './CocktailSearch';

function App() {


  const handleSearch = (searchTerm) => {
    // Define the search API URL
    const apiUrl = `https://www.thecocktaildb.com/api/json/v1/1/search.php?s=${searchTerm}`;

    // Fetch data from the API
    fetch(apiUrl)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        // Assuming data.drinks is an array of cocktail results
        if (data.drinks && data.drinks.length > 0) {
          // Set the first result in the state
          setSearchedCocktail(data.drinks[0]); // Update this line
        } else {
          // Handle the case where no results were found
          setSearchedCocktail(null);
        }
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
        // Handle the error
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Random Cocktail Recipe</h1>
      </header>
      <main>
        <RandomCocktail />
        <CocktailSearch onSearch={handleSearch} />
        {searchedCocktail && <RandomCocktail cocktail={searchedCocktail} />}
      </main>
    </div>
  );
}

export default App;
